#!/usr/bin/env node
const { spawn } = require('child_process');
const path = require('path');
const os = require('os');

// Konfigurasjon for store prosjekter
const PROJECT_ROOT = __dirname;
const MAX_FILE_SIZE = 10 * 1024 * 1024; // 10MB
const EXCLUDED_DIRS = ['node_modules', '.git', 'dist', 'build'];

console.log(`Starting MCP server for AIKI v3 project at: ${PROJECT_ROOT}`);
console.log(`Platform: ${os.platform()}, Files: 21,108, Folders: 996`);

// Start MCP filesystem server
const serverPath = path.join(
  __dirname,
  'node_modules',
  '@modelcontextprotocol',
  'server-filesystem',
  'dist',
  'index.js'
);

const args = [serverPath, PROJECT_ROOT];

const mcpServer = spawn('node', args, {
  stdio: 'inherit',
  env: {
    ...process.env,
    MCP_PROJECT_ROOT: PROJECT_ROOT,
    NODE_OPTIONS: '--max-old-space-size=4096' // 4GB RAM
  }
});

mcpServer.on('error', (err) => {
  console.error('Failed to start MCP server:', err);
  process.exit(1);
});

mcpServer.on('exit', (code) => {
  console.log(`MCP server exited with code ${code}`);
  process.exit(code);
});

// Graceful shutdown
process.on('SIGINT', () => {
  console.log('\nShutting down MCP server...');
  mcpServer.kill('SIGTERM');
});

process.on('SIGTERM', () => {
  mcpServer.kill('SIGTERM');
});
EOF
```

